        // at this point the image is encrypted so we decrypt it        if (!StringUtils.isEmpty(imagePath))            EnDeCrypt.decrypt(new File(imagePath), "this-is-supposed-to-be-a-secure-key");        activity.startActivityForResult(imagingIntent, REQUEST_CODE);    }    @JavascriptInterface    public void openImage(String imageUri){        if(Environment.getExternalStorageState().equals(Environment.MEDIA_MOUNTED)) {            Intent startImage = new Intent(Intent.ACTION_VIEW);            Uri uri = Uri.parse("file://" + Environment.getExternalStorageDirectory().getPath() + imageUri);            startImage.setDataAndType(uri, "image/*");            activity.startActivity(startImage);        }    }    public String getImagePathField() {        return imagePathField;    }    public String getImageCaptionField() {        return imageCaptionField;    }    public static ImageResult parseActivityResult(int requestCode, int resultCode, Intent intent) {        if (requestCode == REQUEST_CODE) {            if (resultCode == Activity.RESULT_OK) {                String sectionName = intent.getStringExtra(KEY_SECTION_NAME);                String imageUri = intent.getStringExtra(KEY_IMAGE_PATH);                String imageCaption = intent.getStringExtra(KEY_IMAGE_CAPTION);                // now that we have an image we encrypt it and keep the path                if (!StringUtils.isEmpty(imageUri))                    EnDeCrypt.encrypt(new File(imageUri), "this-is-supposed-to-be-a-secure-key");                return new ImageResult(sectionName, imageUri, imageCaption);            }        }        return null;    }}